totalSaturatedPeaks=zeros(Nparam1,Nparam2,size(whichPeaks,1)); % count of accumulated discarded peaks due to at least one saturated
totalAllLowPeaks=zeros(Nparam1,Nparam2,size(whichPeaks,1)); % stores the npulses of spectra that has all peaks below a threshold 
totalSinglePeakLow=zeros(Nparam1,Nparam2,size(whichPeaks,1)); % stores the npulses of spectra that has at least one peak below a threshold

% creates strings to name emission lines
lineName = {};
for i=1:size(whichPeaks)
     lineName{i} = [ '#' num2str(whichPeaks(i)) ' ', strjoin(speciesStr(piquees(whichPeaks(i)))), '@',num2str(pila_theoretical(whichPeaks(i)))];
end

if (DISCARD_NONVALID_SPECTRA > 0) 
      fprintf('CF-Step 6: Marking as non-valid weak or saturated spectra (only CF PEAKS in composition checked):\n');
      if (FIRST_SHOT_FOR_AVERAGING>1 || LAST_SHOT_FOR_AVERAGING < Npulses)
         disp(['  Warning!! averaging spectrum only for ', num2str(FIRST_SHOT_FOR_AVERAGING), ':' ,num2str(min(LAST_SHOT_FOR_AVERAGING,Npulses)),' pulses (out of ' ,num2str(Npulses), ' pulses).']);
      end
 for idx2= 1:Nparam2  
  for idx1= 1:Nparam1
      which_spectrum_has_saturated_peaks = {}; 
      %pixels2look are "tuned" looking for higher neighbour pixels, some pixels are on the peak wings well below the max
      tmp_mean_spectra = nanmeanLocal(spectra(:,spectra_is_valid(:,idx1,idx2)==1,idx1,idx2),2);  
      pixels2look = pixels2look_orig;
      for i=1:size(pixels2look_orig,2)
        [maxl,maxi] = max(tmp_mean_spectra(pixels2look_orig(i)-2:pixels2look_orig(i)+2));
        pixels2look(i) = pixels2look_orig(i) + maxi - 3; % the pixel around central with max value
      end
      for npulse=1:Npulses
        if (spectra_is_valid(npulse,idx1,idx2)) % not previously marked, should be all valid
          peaksSat =  spectra_original(pixels2look,npulse,idx1,idx2) > ValSaturation; % lines in composition that are saturated
          numPeaksSat = sum(peaksSat==1);
          allpeaksLow =  spectra_original(pixels2look,npulse,idx1,idx2) < ValMinHeightAllPeaks;
          numPeaksNotLow = sum(allpeaksLow==0);
          singlepeakLow =  spectra_original(pixels2look,npulse,idx1,idx2) < ValMinHeightSinglePeak; % added 19mar2020 v20 
          numPeaksLow = sum(singlepeakLow==1); % number of peaks below the thresdhold
          if (numPeaksSat > 0) % at least one peak in the ORIGINAL non-processed spectrum is saturated
              % for each emission line in composition, for each spectrum with a saturated peak, add one
              totalSaturatedPeaks(idx1,idx2,peaksSat) = totalSaturatedPeaks(idx1,idx2,peaksSat) + 1; % I think the following was wrong, reshape(peaksSat,[1 1 size(peaksSat,1)]);
              discardedSpectraSat(idx1,idx2) = discardedSpectraSat(idx1,idx2)  +1 ; % not really discarded, only marked, this allow to discard a line and get valid spectra for the others, otherwise, a single line in composition mostly saturated will invalidate all spectra
              which_spectrum_has_saturated_peaks {discardedSpectraSat(idx1,idx2)} = npulse; 
          end
          if (numPeaksNotLow == 0) % ALL of the peaks in the ORIGINAL non-processed spectrum are low
              totalAllLowPeaks(idx1,idx2,:) = totalAllLowPeaks(idx1,idx2,:) + reshape(allpeaksLow,[1 1 size(allpeaksLow,1)]);
              discardedSpectraAllLow(idx1,idx2) = discardedSpectraAllLow(idx1,idx2) +1 ;
              spectra_is_valid(npulse,idx1,idx2)=0; 
          end
          if (numPeaksLow > 0 && DISCARD_ALSO_WEAK_PEAKS && spectra_is_valid(npulse,idx1,idx2)~=0) % we want to discard the entire spectrum because at least one peak is too low, ONLY COUNTS SPECTRA NOT DISCARDED AS NOISE
              totalSinglePeakLow(idx1,idx2,:) = totalSinglePeakLow(idx1,idx2,:) + reshape(singlepeakLow,[1 1 size(singlepeakLow,1)]);
              discardedSpectraSinglePeakLow(idx1,idx2) = discardedSpectraSinglePeakLow(idx1,idx2) +1 ;
              spectra_is_valid(npulse,idx1,idx2)=0; 
          end
          if (spectra_is_valid(npulse,idx1,idx2)~=0) % everything's ok
              totalValidSpectra(idx1,idx2) = totalValidSpectra(idx1,idx2) + 1;
          end
          if (spectra_is_valid(npulse,idx1,idx2) ==0) % se ha descartado ahora
              %disp(['  Not valid spectrum(' , num2str(npulse),',',num2str(idx1),',',num2str(idx2),')'  ]);
              %fprintf([num2str(npulse) '-' num2str(idx1) '-' num2str(idx2) ' ']);
          end
        end   
      end % npulse
      % all the Npulses evaluated, are most of them discarded (LOW)?
      if sum(spectra_is_valid(:,idx1,idx2)) < Npulses/5 && spectra(1,1,idx1,idx2) ~= 0 % 80% of spectra in this spatial points have been discarded, do not rise warning if the spectrum is actually empty
          disp(['  Warning!! only ' , num2str(sum(spectra_is_valid(:,idx1,idx2))) ,' valid spectra in spatial point (' , num2str(idx1),',',num2str(idx2),')'  ]);
      end
      
      % new v20 19mar2020: if AVERAGE_ONLY_FIRST > 0 only the N first valid spectra are marked as valid
      if AVERAGE_ONLY_FIRST > 0 
        for n=1:Npulses
          if spectra_is_valid(n,idx1,idx2) 
            for  i=n+AVERAGE_ONLY_FIRST:Npulses
              spectra_is_valid(i,idx1,idx2)=0; % mark as nonvalid everything else
            end
            break
          end
        end
      end
      
      % now we have information to discard completely specific emission lines and yet use the others
      for i=1:size(totalSaturatedPeaks,3)
          if (totalSaturatedPeaks(idx1,idx2,i)>0) % at least one peak of this emission line was saturated
              if  DISCARD_LINE_ABOVE_SAT_PERCENTAGE~= 0 && (discardedSpectraSat(idx1,idx2) >= (totalValidSpectra(idx1,idx2) * DISCARD_LINE_ABOVE_SAT_PERCENTAGE/100)) % the number of spectra with saturated peaks is more than 50%, it is better to avoid using this peak... 
                disp(['  Completely discarding line #' num2str(whichPeaks(i)) ' of ', strjoin(speciesStr(piquees(whichPeaks(i)))), '@',num2str(pila_theoretical(whichPeaks(i))), ' (',num2str(idx1),',',num2str(idx2),') with ' , num2str(totalSaturatedPeaks(idx1,idx2,i)), ' saturated spectra out of ', num2str(totalValidSpectra(idx1,idx2))]);
                doNotUse(whichPeaks(i))=0; 
              else % ... otherwise, it is better to unvalid only the saturated spectra
                str_spectra = '';
                str_comma = '';
                for n=1:totalSaturatedPeaks(idx1,idx2,i)  
                    spectra_is_valid(which_spectrum_has_saturated_peaks{n},idx1,idx2)=0;
                    %OJO!!!!    totalValidSpectra(idx1,idx2) = totalValidSpectra(idx1,idx2) - 1; % was not marked as invalid before
                    str_spectra = strcat(str_spectra,strcat(str_comma,num2str(which_spectrum_has_saturated_peaks{n})));
                    str_comma = ',';
                end
                disp(['  Discarding several spectra (', str_spectra, ') of spatial point (' ,num2str(idx1),',',num2str(idx2),') because ' strjoin(speciesStr(piquees(whichPeaks(i)))), '@',num2str(pila_theoretical(whichPeaks(i))), ' (',num2str(idx1),',',num2str(idx2),') has ' , num2str(totalSaturatedPeaks(idx1,idx2,i)), ' saturated spectra out of ', num2str(totalValidSpectra(idx1,idx2))]);
              end
              %disp('not discarding actually');
              
          end
      end
      piUse = piUse & doNotUse;
      spectra_is_valid(1:FIRST_SHOT_FOR_AVERAGING-1,idx1,idx2)  = 0;
      spectra_is_valid(LAST_SHOT_FOR_AVERAGING+1:Npulses,idx1,idx2)  = 0;
      mean_spectra_only_valids(:,idx1,idx2) = nanmeanLocal(spectra(:,spectra_is_valid(:,idx1,idx2)==1,idx1,idx2),2);  %mother of all averages
      % v27 mean_spectra_is_valid mark the valid averaged spectra, so they can be discarded further on
      mean_spectra_is_valid(idx1,idx2) = sum(spectra_is_valid(:,idx1,idx2)==1) ;
       
    % 17mar2020 debugging information
    if (all(DEBUG_DISCARDING==[idx1,idx2])) % shows statistics of spectra and peaks for this particular spatial point
     f=figure(10);
     f.Name='DEBUG_DISCARDING: statistics for peaks discarding';
     bar([ squeeze(totalAllLowPeaks(idx1,idx2,:)) squeeze(totalSinglePeakLow(idx1,idx2,:)) squeeze(totalSaturatedPeaks(idx1,idx2,:)) ] ,'stacked');
     title({['(idx1=', num2str(idx1) , ') Number of times a peak is < ValMinHeightAllPeaks (blue)'],'< ValMinHeightSinglePeak (red) , saturated (yellow)'});
     set(gca,'xtick',[1:size(whichPeaks)],'xticklabel',lineName)
     xtickangle(90);
     if (size(find(spectra_is_valid(:,idx1,idx2)==1),1)>0)
       f=figure(11);
       f.Name='DEBUG_DISCARDING: surviving valid spectra, shouldn''t be any noise spectra ';
       plot(lambdas,spectra_original(:,spectra_is_valid(:,idx1,idx2)==1,idx1,idx2));
       title(['Valid spectra (',num2str(size(find(spectra_is_valid(:,idx1,idx2)==1),1)),') to average for idx1=' num2str(idx1)]);
     end
     if (size(find(spectra_is_valid(:,idx1,idx2)==0),1)>0)
       f=figure(12);
       f.Name='DEBUG_DISCARDING: discarded noise spectra, should be noise';
       plot(lambdas,spectra_original(:,spectra_is_valid(:,idx1,idx2)==0,idx1,idx2));
       title(['NON valid discarded spectra (', num2str(size(find(spectra_is_valid(:,idx1,idx2)==0),1)),') for idx1=' num2str(idx1)]);
     end
     f=figure(13);
     f.Name='DEBUG_DISCARDING: raw height value of peaks for every pulse';
     imagesc(1:Npulses,1:size(whichPeaks),spectra_original(pixels2look,:,idx1,idx2));
     set(gca,'ytick',[1:size(whichPeaks)],'yticklabel',lineName)
     xlabel('number of pulse');
     title( ['Raw height value of peaks for every pulse (idx1=' , num2str(idx1) ,')']);
     colormap(pink);
     caxis( [ 1000 50000]);
   end

  end % idx1
 end % idx2
 
 if (DEBUG_DISCARDING>0) % shows plot of number of valid spectra for all idx1, idx2=0
     f=figure(14);
     f.Name='DEBUG_DISCARDING: evolution of the number of valid spectra in each point';
     clf;
     hold on;
     plot(ejeParam1,discardedSpectraSat(:,1),'LineWidth',2,'Color','r');    
     plot(ejeParam1,discardedSpectraAllLow(:,1),'LineWidth',2,'Color','b');    
     plot(ejeParam1,discardedSpectraSinglePeakLow(:,1),'LineWidth',2,'Color','k');    
     plot(ejeParam1,totalValidSpectra(:,1),'LineWidth',2,'Color','g');    
     title({'Number of spectra with:','at least one saturated peak from composition (red)','all peaks zero (blue)','single peak below threshold (black)','valid spectra (green)'});   
     xlabel(param1Name);
     ylabel('number of spectra out of ');
     hold off;
 end
end

%% CF-Step 7   substract spectra if ALTERNATE_DELAY_WHICH_ONE!=0
% ALTERNATE_DELAY_WHICH_ONE=1 every alternate shot is at different delay. 
% after this step, each spatial point has individual difference spectra
% every two shots, and mean_only_valids() has the MEAN difference spectrum
if ALTERNATE_DELAY_WHICH_ONE>0 && DO_USE_AVANTES==1
 fprintf('CF-Step 7: subtracting MEAN spectra for ALTERNATE_DELAY_WHICH_ONE=');
 if ALTERNATE_DELAY_WHICH_ONE==1
      fprintf('1 (Substract hotter-colder)\n');
 elseif ALTERNATE_DELAY_WHICH_ONE==2
      fprintf('2 (only hotter)\n');     
 elseif ALTERNATE_DELAY_WHICH_ONE==3
      fprintf('3 (only colder)\n');     
 else
      fprintf('??? unknown\n');     
 end
 for idx2= 1:Nparam2  
  for idx1= 1:Nparam1
      hotter_spectra = zeros(size(lambdas,1),1);
      colder_spectra = zeros(size(lambdas,1),1);
      npfirst = max(firstLaserpulse(idx1,idx2),FIRST_SHOT_FOR_AVERAGING);
%      hotter_intensity = sum(spectra(:,npfirst,idx1,idx2),1);
%      colder_intensity = sum(spectra(:,npfirst+1,idx1,idx2),1);
%      if (hotter_intensity > colder_intensity)
%        ; % the first pulse is the hotter (shorter delay) 
%      else
%        npfirst = npfirst + 1; % start always with hotter spectra (shorter delay, larger & hotter spectrum)
%      end
      num_spectra = 0;
      for npulse=npfirst:2:min(LAST_SHOT_FOR_AVERAGING,Npulses)-1
          if (spectra_is_valid(npulse,idx1,idx2)==1 && spectra_is_valid(npulse+1,idx1,idx2)==1)
            diff_spectrum = spectra(:,npulse,idx1,idx2)-spectra(:,npulse+1,idx1,idx2);
            hotter_spectra = hotter_spectra + spectra(:,npulse,idx1,idx2);
            colder_spectra = colder_spectra + spectra(:,npulse+1,idx1,idx2);
            num_spectra = num_spectra+1;
          else
            disp(['  Spectrum (' , num2str(npulse),' or ', num2str(npulse+1),',',num2str(idx1),',',num2str(idx2),') is not valid within AlternateDelay averaging and differencing range, not used'  ]);
          end
      end
      % calc difference of MEAN hotter and colder spectra. It is safer to
      % get the substract right with the mean spectra, not with the two
      % first
      hotter_intensity = sum(hotter_spectra,1);
      colder_intensity = sum(colder_spectra,1);
      if (ALTERNATE_DELAY_WHICH_ONE==1)
        if (hotter_intensity > colder_intensity)
         mean_spectra_only_valids(:,idx1,idx2) = (hotter_spectra - colder_spectra) ./ num_spectra;
        else
         mean_spectra_only_valids(:,idx1,idx2) = (colder_spectra - hotter_spectra) ./ num_spectra; 
         spectra_is_valid(npfirst,idx1,idx2)=0; % this cannot be substracted
         npfirst = npfirst+1; % the hotter one is the next one
        end
      elseif (ALTERNATE_DELAY_WHICH_ONE==2)
           mean_spectra_only_valids(:,idx1,idx2) = hotter_spectra ./ num_spectra;
      elseif (ALTERNATE_DELAY_WHICH_ONE==3)
           mean_spectra_only_valids(:,idx1,idx2) = colder_spectra ./ num_spectra;
      end
      if (0)
          figure;
          subplot(3,1,1);   plot(lambdas,mean_spectra_only_valids(:,idx1,idx2));title('short delay spectrum - hotter');
          subplot(3,1,2);   plot(lambdas,mean_spectra_only_valids(:,idx1+1,idx2));title('long delay spectrum - colder');
          subplot(3,1,3);   plot(lambdas,mean_spectra_only_valids(:,idx1,idx2)- mean_spectra_only_valids(:,idx1+1,idx2));title('subtracted spectrum');
      end
      % now that we know which spectrum is the hotter and the colder, let's
      % substract per-pair. needed to calculate intensity
      if (ALTERNATE_DELAY_WHICH_ONE==1)
       for npulse=npfirst:2:min(LAST_SHOT_FOR_AVERAGING,Npulses)-1
         diff_spectrum = spectra(:,npulse,idx1,idx2)-spectra(:,npulse+1,idx1,idx2);
         spectra(:,npulse,idx1,idx2) = diff_spectrum;
         spectra(:,npulse+1,idx1,idx2) = diff_spectrum;
       end
       elseif (ALTERNATE_DELAY_WHICH_ONE==2)
        for npulse=npfirst:2:min(LAST_SHOT_FOR_AVERAGING,Npulses)-1
         spectra(:,npulse+1,idx1,idx2) = spectra(:,npulse,idx1,idx2); % duplicates hotter
        end
       elseif (ALTERNATE_DELAY_WHICH_ONE==3)
        for npulse=npfirst:2:min(LAST_SHOT_FOR_AVERAGING,Npulses)-1
         spectra(:,npulse,idx1,idx2) = spectra(:,npulse+1,idx1,idx2); % duplicates colder
        end
      end
   end
 end
 
 
end



%% CF-Step 8   remove baseline (emission background) with different algorithms

if (REMOVE_BASELINE== 1)  % method Yu2009
    disp(['CF-Step 8: Removing baseline (emission background) with Yu2009 method...']);
    for idx2= 1:Nparam2
        for idx1= 1:Nparam1
            tmp_spectra = remove_background_Yu2009(lambdas, mean_spectra_only_valids(:,idx1,idx2),0); % last argument could be 1 to plot the corrected spectrum
            mean_spectra_only_valids(:,idx1,idx2) = tmp_spectra;
        end
    end
end
if (REMOVE_BASELINE== 2)  % method airPLS
    disp(['CF-Step 8: Removing baseline (emission background) with airPLS method...']);
    for idx2= 1:Nparam2
        for idx1= 1:Nparam1
            [tmp_spectra airPLSfittedVector] = airPLS(mean_spectra_only_valids(:,idx1,idx2)',10e7, 2, 0.1, 0.5, 50); %
            mean_spectra_only_valids(:,idx1,idx2) = tmp_spectra;
            
        end
    end
end



 %% CF-Step 9   if DISCARD_SPECTRA_INTENSITY_OUTLIERS>0 the integrated intensity of all valid spectra is used to discard outliers. an histogram of intensities is shown if DEBUG_STABILITY=1 

 disp(['CF-Step 9: Removing outlier spatial points based on integrated intensity...']);
 for idx2= 1:Nparam2
     for idx1= 1:Nparam1
         this_intensity=sum(mean_spectra_only_valids(:,idx1,idx2),1);
         integrated_intensity(idx1,idx2) = this_intensity;
     end
 end
 integrated_intensity(isnan(integrated_intensity))=0; % replace NaN with zero
 
 mean_intensity_all_spectra =  sum(sum(integrated_intensity,1),2) / sum(sum(integrated_intensity~=0,1),2); % mean without zero values
 removed_spectra_integrated_intensity = 0;
 for idx2= 1:Nparam2
     for idx1= 1:Nparam1
         if  integrated_intensity(idx1,idx2) < DISCARD_SPECTRA_INTENSITY_THRESHOLD * mean_intensity_all_spectra && integrated_intensity(idx1,idx2) > 0 
             discarded_integrated_intensity(idx1,idx2)=1; % only marked, not actually discarded, only those discarded due to low (but non zero) intensity
             removed_spectra_integrated_intensity = removed_spectra_integrated_intensity + 1;
         end
     end
 end
 
if (DISCARD_SPECTRA_INTENSITY_OUTLIERS )  
    mean_spectra_is_valid(discarded_integrated_intensity==1) = 0; 
    disp([ ' number of spatial points discarded: ' num2str(removed_spectra_integrated_intensity)]); 
else
    disp(' not discarding.');
end 
% we also calculate here the raw intensity of the emission line at wavelength RESIN_LAMBDA_MONITORING , it is intended to detect silicon from epoxy resin, signaling a bad measurement outside the edge of the limpet
if RESIN_LAMBDA_MONITORING  > 0
    for idx2= 1:Nparam2
        for idx1= 1:Nparam1
            resin_intensity(idx1,idx2) = mean_spectra_only_valids(lambda2pixel(lambdas,RESIN_LAMBDA_MONITORING),idx1,idx2);
        end
        
    end
end

if DEBUG_STABILITY
    
   %new v31 intensity for original raw spectra to detect flatness / horizontality issues 
   f=figure(51);
   f.Name = 'Integrated spectral intensity from raw spectra';
   if Nparam2 > 1
       spec=squeeze(mean(mean(spectra_original,1),2));
       imagesc(ejeParam1,ejeParam2,spec(1:Nparam1,1:Nparam2)'); % for a correct figure, matrix should be traspose and AFTER that, change YDir parameter
   else
       plot(ejeParam1,squeeze(mean(mean(spectra_original,1),2)));
   end
   set(gca,'YDir','normal')    % this is needed to put (0,0) in the proper place
   view(0,90); 
   colormap('jet'); % with jet colormap the outliers are clearly shown    
    
   f=figure(33);
   f.Name = 'Number of valid spectra in each spatial point';
   if Nparam2 > 1
       mesh(ejeParam1,ejeParam2,mean_spectra_is_valid','FaceColor','flat'); % for a correct figure, matrix should be traspose and AFTER that, change YDir parameter
   else
       plot(ejeParam1,mean_spectra_is_valid);
   end
   set(gca,'YDir','normal')    
   view(0,90); 
   colormap('jet'); % with jet colormap the outliers are clearly shown
   
   f=figure(34);
   f.Name='Integrated spectral intensity in each spatial point - points below threshold are shown in red';
   ax34a=axes;
   rel_integrated_intensity = integrated_intensity./mean_intensity_all_spectra;
   rel_integrated_intensity(rel_integrated_intensity == 0) = NaN;
   if Nparam2 > 1 
       m34a=mesh(ejeParam1,ejeParam2,(rel_integrated_intensity+discarded_integrated_intensity*3)','FaceColor','flat'); % for a correct figure, matrix should be traspose and AFTER that, change YDir parameter
       pbaspect([abs(ejeParam1(end)) abs(ejeParam2(end)) 1]) % this is the real aspect ratio
   else
       higherRII = rel_integrated_intensity;
       lowerRII = rel_integrated_intensity; 
       higherRII(higherRII<DISCARD_SPECTRA_INTENSITY_THRESHOLD)=NaN; % this is a trick to plot two colors
       %lowerRII(lowerRII>DISCARD_SPECTRA_INTENSITY_THRESHOLD)=NaN;
       hold on;
       plot(ejeParam1,lowerRII,'r-');
       plot(ejeParam1,higherRII,'b-');
       %plot(ejeParam1([1,25]),[DISCARD_SPECTRA_INTENSITY_THRESHOLD,DISCARD_SPECTRA_INTENSITY_THRESHOLD],'r-')
       hold off;
   end
   set(gca,'YDir','normal')
   view(0,90); 
   colormap(ax34a,'jet');
   
   if RESIN_LAMBDA_MONITORING  > 0
       f=figure(35);
       f.Name=['Resin wavelength monitoring @' num2str(RESIN_LAMBDA_MONITORING) ' min=' num2str(min(min(resin_intensity))) ' max=' num2str(max(max(resin_intensity))) ];
       if Nparam2 > 1
         mesh(ejeParam1,ejeParam2,resin_intensity','FaceColor','flat'); % for a correct figure, matrix should be traspose and AFTER that, change YDir parameter
       else
         plot(ejeParam1,resin_intensity);
       end
       set(gca,'YDir','normal')
       view(0,90);
       colormap('jet');
   end
   
   %histograms of valid spectra and intensity values
   f=figure(36);
   f.Name='Histogram of valid spectra in each spatial points';
   histogram(mean_spectra_is_valid(mean_spectra_is_valid>0));
   set(gca,'Xtick',0:Npulses)

   %histograms of intensity values
   f=figure(37);
   f.Name='Histogram of intensities in each spatial points (1=mean)';
   histogram(rel_integrated_intensity(rel_integrated_intensity>0));
   
end


%%%% new v30 fit excursion parameters are obtained in a first step, using ANY spatial point or an average of any of them
disp(['CF-Step 10: fitting zero: calc of peak excursions and seed fitting parameters...']);

if WHICH_SPECTRA_FOR_FITTING_ZERO == 0 % average of ALL spectra
    howmany = 0;
    for idx2= 1:Nparam2
        for idx1= 1:Nparam1
            if mean_spectra_is_valid(idx1,idx2) > 0 % v27 if there is at least one valid spectrum
              if howmany==0
                  which_spectrum = mean_spectra_only_valids(:,idx1,idx2);
                  howmany=1;
              else
                  which_spectrum = which_spectrum + mean_spectra_only_valids(:,idx1,idx2);
                  howmany = howmany +1;
              end
            end
        end
    end
    which_spectrum = which_spectrum ./ howmany;
    disp(['    Using an average of ' num2str(howmany) ' spectra.']);
 else
    which_spectrum = mean_spectra_only_valids(:,WHICH_SPECTRA_FOR_FITTING_ZERO,1); % this spatial point is used
    disp(['    Using one spectrum from idx1=' num2str(WHICH_SPECTRA_FOR_FITTING_ZERO) ',idx2=1']);
 end


for p=1:CFNlines
    if (piUse(p)==1 && pila(p)>= lambdas(1) && pila(p)<=lambdas(end)  ) % marked to be used, AND in composition, P==1 (Halways fitted)
        % v22: if WHICH_SPECTRA_FOR_FITTING_ZERO=1, the first calc for pixleftbkg1, pixrightbkg1 is used ; v24: extended to bkg2
        if (piquees(p)==Halfa)
            widthCriteriaForDiscard=40; % for discarding, Halfa peak stark broadening is much larger. 18ene2022: upper bound for FWHM peak fitting is too low for normal peaks and Halfa, changed from 20 to 40
            HalfaLargerWidthCorrection=7; % factor to use a larger wavelength range for (much wider) Halfa line
            fit_excursion=25; % FWHM times, how far the peak is considered to extend, max, tested with Halfa 28feb2020, UPDATE: 9ene2021 for PD.541 experiments was too large, changed from 30 to 20; 20abril2021 for pastillonCF was too small, changed to 25
        else
            widthCriteriaForDiscard=10; % 18ene2022: upper bound for FWHM peak fitting is too low for normal peaks and Halfa, changed from 5 to 10
            HalfaLargerWidthCorrection=1; % for "normal" peaks
            fit_excursion=4; %N times instrumental FWHM v24 changed from 8 to 15, was too small for some peaks %v30.6 testing lower values more suitable for averaged spectrum %v31 changed from 2 to 3, CHANGED TO 4
        end
        % FIT-Step1 first calc an estimated center, and the extend of the peak. Central pixel is searched up to 2 pixels left and right to compensate for miscalibration. piobs() could be used to compensate larger shifts
        pixcentral=lambda2pixel(lambdas,pila(p));
        [maxl,maxi] = max(which_spectrum(pixcentral-2:pixcentral+2));
        pixcentral = pixcentral + maxi - 3; % the pixel around central with max value
        heightcentral = which_spectrum(pixcentral);
        
        % new! v24 30jun2021 the max excursion is performed over the spectrally-filtered spectrum (savitzky golay), but fitting  should be done with the original averaged one
        filtered_spectrum = which_spectrum;
        if (SAVITZKY_GOLAY_PEAK_EXTENSION_ORDER > 0)
            filtered_spectrum = sgolayfilt(filtered_spectrum,SAVITZKY_GOLAY_PEAK_EXTENSION_ORDER,5); % best result for me is with order 1 frame 5
        end
        
        pixleft=lambda2pixel(lambdas,lambdas(pixcentral) - 0.5*getSpectrometerFWHM(lambdas(pixcentral))*HalfaLargerWidthCorrection); % starting search point is FWHM/2
        max_excursion = pixleft - abs(lambda2pixel(lambdas,lambdas(pixcentral)+fit_excursion*getSpectrometerFWHM(lambdas(pixcentral)))-lambda2pixel(lambdas,lambdas(pixcentral))); % number of pixels max for peak extend
        while(pixleft>1 && pixleft>max_excursion && filtered_spectrum(pixleft)>heightcentral/40) % stop if we are too far or intensity is <2.5%
            if(filtered_spectrum(pixleft)<filtered_spectrum(pixleft-1)) %once it starts to increase again...
                break;
            end
            pixleft=pixleft-1;
        end
        pixright=lambda2pixel(lambdas,lambdas(pixcentral)+ 0.5*getSpectrometerFWHM(lambdas(pixcentral))*HalfaLargerWidthCorrection); % starting search point is FWHM/2
        max_excursion = pixright + abs(lambda2pixel(lambdas,lambdas(pixcentral)+fit_excursion*getSpectrometerFWHM(lambdas(pixcentral)))-lambda2pixel(lambdas,lambdas(pixcentral))); % number of pixels max for peak extend
        while(pixright<Npixeles-1 && pixright<max_excursion && filtered_spectrum(pixright)>heightcentral/40) % stop if we are too far or intensity is <5%
            if(filtered_spectrum(pixright)<filtered_spectrum(pixright+1)) %once it starts to increase again...
                break;
            end
            pixright=pixright+1;
        end
        % FIT-Step2 we have a [pixleft,pixright] window for peak FITTING, now we search a background zone , stop when intensity builds up; it is repited twice, after first iteration, peak extend is reevaluated if a point of the peak is below the baseline
        for bf=1:2
            pixleftbkg1=pixleft;
            pixleftbkg2=pixleft;
            max_excursion = pixleftbkg1 - abs(lambda2pixel(lambdas,lambdas(pixcentral)+fit_excursion*getSpectrometerFWHM(lambdas(pixcentral)))-lambda2pixel(lambdas,lambdas(pixcentral))); % max pixel to the left of background
            while(pixleftbkg2>1 && pixleftbkg2>max_excursion && (filtered_spectrum(pixleftbkg2) - filtered_spectrum(pixleftbkg1)) < heightcentral/10) % stop when intensity builds up
                pixleftbkg2=pixleftbkg2-1;
            end
            pixrightbkg1=pixright;
            pixrightbkg2=pixright;
            max_excursion = pixrightbkg1 + abs(lambda2pixel(lambdas,lambdas(pixcentral)+fit_excursion*getSpectrometerFWHM(lambdas(pixcentral)))-lambda2pixel(lambdas,lambdas(pixcentral))); % max pixel to the right of background
            while(pixrightbkg2<Npixeles-1 && pixrightbkg2<max_excursion && (filtered_spectrum(pixrightbkg2) - filtered_spectrum(pixrightbkg1)) < heightcentral/10) % stop when intensity builds up
                pixrightbkg2=pixrightbkg2+1;
            end
            %FIT-Step3 linear fit of the baseline with background points (over pixels space)
            % pBkg = fit([lambdas(pixleftbkg2:pixleftbkg1); lambdas(pixrightbkg1:pixrightbkg2)], [mean_spectra_only_valids(pixleftbkg2:pixleftbkg1,idx1,idx2); mean_spectra_only_valids(pixrightbkg1:pixrightbkg2,idx1,idx2)],'poly2' );
            % linear with two points
            % pBkg = fit([lambdas(pixleftbkg2:pixleftbkg1); lambdas(pixrightbkg1:pixrightbkg2)], [mean_spectra_only_valids(pixleftbkg2:pixleftbkg1,idx1,idx2); mean_spectra_only_valids(pixrightbkg1:pixrightbkg2,idx1,idx2)],'poly2' );
            % v30.6 from now on, with individual spectrum, not the filtered one
            pBkgPx = fit([pixleftbkg1; pixrightbkg1], [which_spectrum(pixleftbkg1); which_spectrum(pixrightbkg1)],'poly1' ); % before v25 was the formula used, only take into account two points
            % WARNING!! the same baseline fit should be done AGAIN later on if WHICH_SPECTRA_FOR_FITTING_ZERO==1
            % pBkgPx = fit([pixleftbkg2:pixleftbkg1  pixrightbkg1:pixrightbkg2]', [mean_spectra_only_valids(pixleftbkg2:pixleftbkg1,idx1,idx2)'  mean_spectra_only_valids(pixrightbkg1:pixrightbkg2,idx1,idx2)']','poly1' ); %v25 I believe now that background baseline should be get this way, but the baselines are worse
            % the baseline over lambda is not really needed
            % pBkgLa = fit([lambdas(pixleftbkg1); lambdas(pixrightbkg1)], [mean_spectra_only_valids(pixleftbkg1,idx1,idx2); mean_spectra_only_valids(pixrightbkg1,idx1,idx2)],'poly1' ); % baseline in lambda space, needed to interpolate at the central wavelenth
            
            %FIT-Step4 corrects peak extend IF peak wings points are UNDER the baseline
            %DEBUG:  plot(pixleft:pixright,mean_spectra_only_valids(pixleft:pixright,idx1,idx2),pixleft:pixright,feval(pBkg,pixleft:pixright)); title([strjoin(speciesStr(piquees(p))),'@' num2str(pila(p))]);
            baselineOk=true;
            for i=pixcentral:-1:pixleft
                if(which_spectrum(i) < feval(pBkgPx,i)*0.99) % if the wing of the peak is below the interpolated baseline
                    pixleft=i;
                    baselineOk=false;
                    break; % look no further
                end
            end
            for i=pixcentral:1:pixright
                if(which_spectrum(i) < feval(pBkgPx,i)*0.99) % if the wing of the peak is below the interpolated baseline
                    pixright=i;
                    baselineOk=false;
                    break; % look no further
                end
            end
            if baselineOk
                break; % no need to reevaluate
            end
        end % for 1:2 reevaluate if peak extend is reduced
        
        % we have got the excursion parameters for this line fro FIT
        peaks_fit_pixcentral0(p)=pixcentral;
        peaks_fit_pixright0(p)=pixright;
        peaks_fit_pixleft0(p)=pixleft;
        peaks_fit_pixrightbkg10(p)=pixrightbkg1;
        peaks_fit_pixleftbkg10(p)=pixleftbkg1;
        peaks_fit_pixrightbkg20(p)=pixrightbkg2;
        peaks_fit_pixleftbkg20(p)=pixleftbkg2; % "far" background points are not currently used for fitting the baseline
        
        % lorentzian fitting
        peak_lambdas = lambdas(pixleft:pixright);
        if (size(peak_lambdas,1)>4) % 28feb2020 if the area has no peak and just a smooth valley, pixleft=pixright=pixcentral
            peak_intensities =  which_spectrum(pixleft:pixright); % the peak
            peak_intwobaseline = which_spectrum(pixleft:pixright) - feval(pBkgPx,pixleft:pixright); % the peak minus interpolated baseline
            
            %v26 deconvolution should be done here
            if DO_DECONVOLUTION
                pause(0.1);
            end
            
            ws = warning('off','all');  % Turn off warning messages of lorentzfit
            boundp3lb = (getSpectrometerFWHM(pila(p))/2)^2;
            boundp3hb = (widthCriteriaForDiscard*getSpectrometerFWHM(pila(p))/2)^2; % new v28 upper limit is widthCriteriaForDiscard times instrumental broadening, different for Halfa
            bounds = [-Inf -Inf boundp3lb  ;  +Inf +Inf boundp3hb ];
            [yprime1,loparams,resnorm1,residual1]=lorentzfit(double(peak_lambdas),double(peak_intwobaseline),[],bounds,'3',opts);warning(ws)  % Turn it back on.
            %end
            %[FitResults,FitError] = peakfit([peak_lambdas peak_intwobaseline],lambdas(pixcentral),2.0,1,2,0,99,0,0,0,0); % peak fitting by the Tom O'haver, 11th parameter is plot
            % store fitting error (relative, %)
            
            
            peaks_fit_error_frommean0(p) = 100*norm(residual1) / sqrt(size(peak_intensities,1)) / max(peak_intensities); % in percentage, same metric as peakfit() from Tom O'Haver
            %peaks_fit_error_frommean(p,idx1,idx2) = FitError(1);
            if (peaks_fit_error_frommean0(p)>20) % the fitting error is too large
                disp(['      Warning!! fitting error of line #' num2str(p) ' of ', strjoin(speciesStr(piquees(p))), '@',num2str(pila_theoretical(p)), ' (fit excursion) is ' , num2str(peaks_fit_error_frommean0(p)), '%']);
            end
               
            %new v30.6 first fitting parameters could be the seed for 
            peaks_fit_FitZeroLorentzParams(p,:) = loparams;            
            
            % store height
            peaks_heights_frommean0(p)=loparams(1)/loparams(3); % +loparams(4); % total height
            peaks_rawHeights_frommean0(p) = heightcentral; %*new* v30.7 raw raw super-raw height, just to see minimum-possible sequence variability
            
            %peaks_heights_frommean(p,idx1,idx2)=FitResults(3);
            % store central lambda
            peaks_central_frommean0(p)=loparams(2);
            %peaks_central_frommean(p,idx1,idx2)=FitResults(2);
            error_lambda_central = peaks_central_frommean0(p)-pila(p);
            if (abs(error_lambda_central) > 2.0*getSpectrometerFWHM(pila(p))) % the error in lambda_central is too large
                disp(['      Warning!! central wavelength error for fitting of line #' num2str(p) ' of ', strjoin(speciesStr(piquees(p))), '@',num2str(pila_theoretical(p)), ' (fit excursion) is ' , num2str(error_lambda_central), 'nm']);
            end
            % store total fwhm width and calculate Stark width
            % 14dic2018 there are two possible methods: cuadratic and linear,according to LIBS-tutorial-partII only cuadratic is valid
            %peaks_stark_frommean(selectedPeaks,idx1,idx2) = -(getSpectrometerFWHM(lampara_FWHM,pila(selectedPeaks)).^2 - peaks_fwhm_frommean(selectedPeaks,idx1,idx2).^2)./peaks_fwhm_frommean(selectedPeaks,idx1,idx2);
            peaks_fwhm_frommean0(p)=2*sqrt(loparams(3));
            %peaks_fwhm_frommean(p,idx1,idx2)=FitResults(4);
            % 14dic2018 we should test different approaches: linear subs, cuadratic, torres2007 formula...
            % instrumental deconvolution should be done here
            %xxxxxx OJO!! esta fórmula de stark no puede estar bien
            peaks_fwhmSpectrometer_frommean0(p) = getSpectrometerFWHM(pila(p));
            peaks_stark_frommean0(p) = -(getSpectrometerFWHM(pila(p)).^2 - peaks_fwhm_frommean0(p).^2)./peaks_fwhm_frommean0(p);
            %disp("ojo!");
            %peaks_stark_frommean(p,idx1,idx2) = sqrt(peaks_fwhm_frommean(p,idx1,idx2).^2 - getSpectrometerFWHM(pila(p)).^2 );
            if ( peaks_stark_frommean0(p) < 0 || ~isreal(peaks_stark_frommean0(p)) )
                peaks_stark_frommean0(p) = 0; % we can get negative values, remove them
                disp(['      Warning!! stark width is less than zero for fitting of line #' num2str(p) ' of ', strjoin(speciesStr(piquees(p))), '@',num2str(pila_theoretical(p)), ' (fit excursion)']);
            end
            % calculate and store AREA of the peak
            % new v21 5ago2020 yo can choose wich method to use with the variable AREA_METHOD =
            oversampled_lambdas = lambdas(pixleft):0.001:lambdas(pixright);
            oversampled_lorentzPeak = loparams(1)./((oversampled_lambdas - loparams(2)).^2 + loparams(3)); % oversampled lorentzian shape
            if AREA_METHOD==0 % analytical formula of modeled lorentzian peak
                peaks_area_frommean0(p)= pi*peaks_heights_frommean0(p)*peaks_fwhm_frommean0(p)/2; % area of the lorentzian function, according to Demtröder, ec. 3.10d, p. 64
            elseif AREA_METHOD==1 % trapezoidal integration of the oversampled modeled lorentzian peak
                peaks_area_frommean0(p)= trapz(oversampled_lambdas,oversampled_lorentzPeak);
            elseif AREA_METHOD==2 % two halves area from modelled lorentzian peak
                %the area of the right half could be a better estimation for heavily asymmetric peaks (some channels of our spectrometer have this problem)
                %5ago2020: last thought is that, as coma aberration does not change the total area (energy conservation), either sides of the modeled peak are wrong
                idx_central = find(oversampled_lorentzPeak== max(oversampled_lorentzPeak));
                half_osl = oversampled_lambdas(idx_central:end);
                half_osi = oversampled_lorentzPeak(idx_central:end);
                peaks_twohalfareas_frommean0(p)= 2*trapz(half_osl,half_osi);
                peaks_area_frommean0(p)= peaks_twohalfareas_frommean0(p);
            elseif AREA_METHOD==3 % trapezoidal integration of the NOT oversampled baseline-corrected SPECTRA
                peaks_area_frommean0(p)= trapz(peak_lambdas,peak_intwobaseline );
            else
                error('AREA_METHOD not yet implemented');
            end
            
            %27nov2018 sometimes the fitting produces complex numbers, when there is not clear peaks
            if (~isreal(peaks_fwhm_frommean0(p)))
                %figure(29);    plot(trozo_lambdas,trozo_pico,trozo_lambdas,yprime1)
                %disp('imaginario!');
                peaks_fwhm_frommean0(p) = 0;
            end
        else % no enough points for fitting
            peaks_heights_frommean0(p)=0; % will be rendered invalid
        end
        
        if DEBUG_FITTING_ZERO==1
            f=figure(16);clf;hold on;
            f.Name='DEBUG_FITTING_ZERO: peak fitting';
            plot(lambdas(pixleftbkg2:pixrightbkg2),which_spectrum(pixleftbkg2:pixrightbkg2),'b'); title([strjoin(speciesStr(piquees(p))),'@' num2str(pila_theoretical(p))]); % the original peak
            plot(lambdas(pixleft),which_spectrum(pixleft),'r*'); % extend of peak for fitting
            plot(lambdas(pixright),which_spectrum(pixright),'r*');
            plot(lambdas(pixleftbkg2),which_spectrum(pixleftbkg2),'g+'); % baseline extend
            plot(lambdas(pixrightbkg2),which_spectrum(pixrightbkg2),'g+');
            plot(lambdas(pixleftbkg2:pixrightbkg2),feval(pBkgPx,pixleftbkg2:pixrightbkg2),'r--'); % interpolated baseline
            if(size(peak_lambdas,1)>4) % fitting is not performed for less than 5 points, next line would give am error
                plot(peak_lambdas,yprime1,'g');  % the fitting
                plot(oversampled_lambdas,oversampled_lorentzPeak,'k'); % the oversampled lorentzian peak
                plot(peak_lambdas,peak_intwobaseline,'m.');  % the peaks minus baseline
                disp(['      ',strjoin(speciesStr(piquees(p))), sprintf('@%3.2f',pila_theoretical(p)),'> height=', sprintf('%5.0f',peaks_heights_frommean0(p)), '   measured width=', sprintf('%1.4f',peaks_fwhm_frommean0(p)),'   stark width=', sprintf('%1.4f',peaks_stark_frommean0(p)),'   area=',sprintf('%5.1f',peaks_area_frommean0(p)),'   area(2xhalf)=',sprintf('%5.1f',peaks_twohalfareas_frommean0(p)),'   error=',sprintf('%2.2f',peaks_fit_error_frommean0(p)),'%'  ]);
                hold off;
                f=figure(17);clf;hold on;
                f.Name='DEBUG_FITTING_ZERO: alternate peak fitting with peakfit';
                peakfit([peak_lambdas peak_intwobaseline],lambdas(pixcentral),2.0,1,2,0,99,0,0,0,1); % peak fitting by the Tom O'haver
            end
            pause;
        end
    end % if piusar (we want to use that peak)
end % p peaks


if DEBUG_FITTING_ZERO>0
    figure(15);  % filtered spectrum with emission lines
    %plot(lambdas,mean_spectra_only_valids(:,1,1),'k'); 
    plot(lambdas,which_spectrum .* dividir_respuesta_espectrometro,'k'); % without irrandiance correction
    labelcolors = {'b','g','r','m','c','k','b','g','r','m','c','k','b','g','r','m','c','k','b','b','g','r','m','c','k','b','g','r','m','c','k','b','g','r','m','c','k','b'};
    %labelcolorsDim = {[0.5 0.5 1],[0.5 1 0.5],[1 0.5 0.5],[1 0.5 1],'c','y','b','g','r','m','c','y'};
    cidx=1;
    for s = composition % I couldn't parallelize it, gives "Passing multiple values to ConstantLine is not supported." in R2020a
        for idx = find(piquees==s)'
            %idxs = find(piquees==s);
            %p_lambdas = pila(idxs);
