        if mean_spectra_is_valid(idx1,idx2) > 0 % v27 if there is at least one valid spectrum
        %if (~isnan(mean_spectra_only_valids(1,idx1,idx2))) % mean spectrum from non-data gives NaN
          disp(['   Doing peak fitting for (', num2str(idx1),',',num2str(idx2),')']);
          for p=1:CFNlines
           if (piUse(p)==1 && pila(p)>= lambdas(1) && pila(p)<=lambdas(end)  ) % marked to be used, AND in composition, P==1 (Halways fitted)

             % v30: everything about peak excursion is already calculated  
             pixcentral = peaks_fit_pixcentral0(p);
             pixright = peaks_fit_pixright0(p);
             pixleft = peaks_fit_pixleft0(p);
             pixrightbkg1 = peaks_fit_pixrightbkg10(p);
             pixleftbkg1 = peaks_fit_pixleftbkg10(p);
             pixrightbkg2 = peaks_fit_pixrightbkg20(p);
             pixleftbkg2 = peaks_fit_pixleftbkg20(p);
             pBkgPx = fit([pixleftbkg1; pixrightbkg1], [mean_spectra_only_valids(pixleftbkg1,idx1,idx2); mean_spectra_only_valids(pixrightbkg1,idx1,idx2)],'poly1' ); % before v25 was the formula used, only take into account two points
             %v26 with 2D grid scanning, the first fit could not be (1,1)
        
       
             % lorentzian fitting
             peak_lambdas = lambdas(pixleft:pixright);
             if (size(peak_lambdas,1)>4) % 28feb2020 if the area has no peak and just a smooth valley, pixleft=pixright=pixcentral
               peak_intensities =  mean_spectra_only_valids(pixleft:pixright,idx1,idx2); % the peak
               peak_intwobaseline = mean_spectra_only_valids(pixleft:pixright,idx1,idx2) - feval(pBkgPx,pixleft:pixright); % the peak minus interpolated baseline
               
               %v26 deconvolution should be done here
               if DO_DECONVOLUTION
                   pause(0.1);
               end

               ws = warning('off','all');  % Turn off warning messages of lorentzfit
               boundp3lb = (getSpectrometerFWHM(pila(p))/2)^2;
               boundp3hb = (widthCriteriaForDiscard*getSpectrometerFWHM(pila(p))/2)^2; % new v28 upper limit is widthCriteriaForDiscard times instrumental broadening, different for Halfa
               bounds = [-Inf -Inf boundp3lb  ;  +Inf +Inf boundp3hb ];
               
               % v30.6 P0 = FitZeroLorentzParams
               %[yprime1,loparams,resnorm1,residual1]=lorentzfit(double(peak_lambdas),double(peak_intwobaseline),[],bounds,'3',opts);warning(ws)  % Turn it back on.
               [yprime1,loparams,resnorm1,residual1]=lorentzfit(double(peak_lambdas),double(peak_intwobaseline),peaks_fit_FitZeroLorentzParams(p,:),bounds,'3',opts);warning(ws)  % Turn it back on.
                %[FitResults,FitError] = peakfit([peak_lambdas peak_intwobaseline],lambdas(pixcentral),2.0,1,2,0,99,0,0,0,0); % peak fitting by the Tom O'haver, 11th parameter is plot
               peaks_fit_error_frommean(p,idx1,idx2) = 100*norm(residual1) / sqrt(size(peak_intensities,1)) / max(peak_intensities); % in percentage, same metric as peakfit() from Tom O'Haver
               %peaks_fit_error_frommean(p,idx1,idx2) = FitError(1);
               if (peaks_fit_error_frommean(p,idx1,idx2)>20) % the fitting error is too large
                   disp(['      Warning!! fitting error of line #' num2str(p) ' of ', strjoin(speciesStr(piquees(p))), '@',num2str(pila_theoretical(p)), ' (',num2str(idx1),',',num2str(idx2),') is ' , num2str(peaks_fit_error_frommean(p,idx1,idx2)), '%']);
               end
               
               %new v28 uses last params as seed 
               peaks_fit_lastLorentzParams(p,:) = loparams;
               
               % store height
               peaks_heights_frommean(p,idx1,idx2)=loparams(1)/loparams(3); % +loparams(4); % total height
               peaks_rawHeights_frommean(p,idx1,idx2) = mean_spectra_only_valids(peaks_fit_pixcentral0(p),idx1,idx2); %*new* v30.7 raw height to check sequence variability
               %peaks_heights_frommean(p,idx1,idx2)=FitResults(3);
               % store central lambda
               peaks_central_frommean(p,idx1,idx2)=loparams(2);
               %peaks_central_frommean(p,idx1,idx2)=FitResults(2);
               error_lambda_central = peaks_central_frommean(p,idx1,idx2)-pila(p);
               if (abs(error_lambda_central) > 2.0*getSpectrometerFWHM(pila(p))) % the error in lambda_central is too large               
                   disp(['      Warning!! central wavelength error for fitting of line #' num2str(p) ' of ', strjoin(speciesStr(piquees(p))), '@',num2str(pila_theoretical(p)), ' (',num2str(idx1),',',num2str(idx2),') is ' , num2str(error_lambda_central), 'nm']);
               end
               % store total fwhm width and calculate Stark width
               % 14dic2018 there are two possible methods: cuadratic and linear,according to LIBS-tutorial-partII only cuadratic is valid
               %peaks_stark_frommean(selectedPeaks,idx1,idx2) = -(getSpectrometerFWHM(lampara_FWHM,pila(selectedPeaks)).^2 - peaks_fwhm_frommean(selectedPeaks,idx1,idx2).^2)./peaks_fwhm_frommean(selectedPeaks,idx1,idx2);
               peaks_fwhm_frommean(p,idx1,idx2)=2*sqrt(loparams(3)); 
               %peaks_fwhm_frommean(p,idx1,idx2)=FitResults(4); 
              % 14dic2018 we should test different approaches: linear subs, cuadratic, torres2007 formula...
