    eCO.Chart.ChartTitle.Text = ['Ratio '  WHICH_RATIO_TO_EXPORT  ' '  EXPERIMENT_NAME];
    eWs.Save;
    eWs.Close(false);
    e.Quit;
    delete(e);
    end
